$(function(){$("#toggle-all").change(function(a){$("input[type=checkbox]").attr("checked",$(this).is(":checked"))});$(".document-view.content img.thumb").popover({html:true,trigger:"hover"});$("#contents-table").tableDnD({onDrop:function(d,f){var e=d.tBodies[0].rows;var c=parseInt(f.id,10);var b=parseInt(f.id,10);for(var a=0;a<e.length;a++){if(parseInt(e[a].id,10)==c){b=a;break}}$.post("move-child-position",{from:c,to:b},function(g){if(g.result=="success"){for(var h=0;h<e.length;h++){e[h].id=h}}else{for(var h=0;h<e.length;h++){$("tr#"+h).appendTo("#contents-table tbody")}alert("Reordering not successful, previous order has been restored.")}})}})});